sys_script_include.script:
var Data_Processor = Class.create();

Data_Processor.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    saveFormData: function() {
		var result = this.newItem('result');
        result.setAttribute('handler', 'saveFormData');	

        var form_data = this.getParameter('sysparm_form_data');
        var form_desc = this.getParameter('sysparm_form_desc');
		var form_id = this.getParameter('sysparm_form_id');

        var gr = new GlideRecord('x_159204_fb_forms');
		gr.addQuery('sys_id', form_id);
		gr.query();

		if (gr.next()) {
			gr.name = form_desc;
			gr.form_data = form_data;

			if (gr.update()) {
				gs.addInfoMessage('Form updated.');
			}
			else {
				gs.addErrorMessage('Could not update form.');
			}
		}
		else {
			gr.initialize();
			gr.name = form_desc;
			gr.form_data = form_data;
			var record_id = gr.insert();

			if (record_id) {			
				result.setAttribute('form_id', record_id);			
			}
			else {
				gs.addErrorMessage('Could not create new form.');
			}			
		}
    },

    loadFormData: function() {
        var result = this.newItem('result');
        result.setAttribute('handler', 'loadFormData');

        var form_id = this.getParameter('sysparm_form_sysid');

        var gr = new GlideRecord('x_159204_fb_forms');
        gr.get(form_id);

        if (gr) {
            result.setAttribute('form_desc', gr.name.toString());
            result.setAttribute('form_data', gr.form_data.toString());
        }

    },

    type: 'Data_Processor'
});
