sys_ui_page.client_script:
var form_id = '';

// need to wait extra time for loading in order to get the request parameters
window.setTimeout(preload, 800);

function preload() {
    var gUrl = new GlideURL();
    gUrl.setFromCurrent();
    form_id = gUrl.getParam("form_id");

    var fields = [{
        label: "Email",
        type: "text",
        subtype: "email",
        icon: "✉"
    }];

    //var formBuilder = $(fbEditor).formBuilder(fields);

    if (form_id) {
        var ga = new GlideAjax('x_159204_fb.Data_Processor');
        ga.addParam('sysparm_name', 'loadFormData');
        ga.addParam('sysparm_form_id', form_id);

        ga.getXML(renderForm);
    }
}

function renderForm(response) {

	var results = response.responseXML.getElementsByTagName("result");
	var form_data = results[0].getAttribute("form_data");
	document.getElementById('form_desc').value = results[0].getAttribute("form_desc");

	var fbEditor = document.getElementById("build-wrap");

    var options = {
        disabledActionButtons: ['data'],
		formData: form_data,
        onSave: function(evt, form_data) {

            var form_desc = document.getElementById('form_desc').value;

            var ga = new GlideAjax('x_159204_fb.Data_Processor');
            ga.addParam('sysparm_name', 'saveFormData');
            ga.addParam('sysparm_form_data', form_data);
            ga.addParam('sysparm_form_desc', form_desc);
			ga.addParam('sysparm_form_id', form_id);

            ga.getXML(saveForm);
        },
		fields: [
			{
				label: 'Email',
				type: 'text',
				subtype: 'email',
				icon: "✉"
			},
			{
				label: 'Phone',
				type: 'text',
				subtype: 'phone',
				icon: ''
			}
		],
        inputSets: [{
            label: 'Requester Details',
            name: 'requester-details', // optional - one will be generated from the label if name not supplied
            showHeader: true, // optional - Use the label as the header for this set of inputs
            fields: [{
                    type: 'text',
                    label: 'Name',
                    className: 'form-control'
                },
                {
                    type: 'text',
                    label: 'E-mail address',
                    className: 'form-control'
                },
                {
                    type: 'text',
                    label: 'Phone number',
                    className: 'form-control'
                },
            ]
        }, ]
    };

	$(fbEditor).formBuilder(options);
}

function saveForm(response) {
    var results = response.responseXML.getElementsByTagName("result");

    if (results) {
        if (results[0].getAttribute('saved') == 'true') {
            var x = document.getElementById("snackbar");

            // Add the "show" class to DIV
            x.className = "show";

            // After 3 seconds, remove the show class from DIV
            setTimeout(function() {
                x.className = x.className.replace("show", "");
            }, 3000);

        }
	}
}

function buildOptions() {
    var options = {
        disabledActionButtons: ['data'],
		formData: form_data,
        onSave: function(evt, form_data) {

            var form_desc = document.getElementById('form_desc').value;

            var ga = new GlideAjax('x_159204_fb.Data_Processor');
            ga.addParam('sysparm_name', 'saveFormData');
            ga.addParam('sysparm_form_data', form_data);
            ga.addParam('sysparm_form_desc', form_desc);
			ga.addParam('sysparm_form_id', form_id);

            ga.getXML(parseResults);
        },
		fields: [
			{
				label: 'Email',
				type: 'text',
				subtype: 'email',
				icon: "✉"
			},
			{
				label: 'Phone',
				type: 'text',
				subtype: 'phone',
				icon: ''
			}
		],
        inputSets: [{
            label: 'Requester Details',
            name: 'requester-details', // optional - one will be generated from the label if name not supplied
            showHeader: true, // optional - Use the label as the header for this set of inputs
            fields: [{
                    type: 'text',
                    label: 'Name',
                    className: 'form-control'
                },
                {
                    type: 'text',
                    label: 'E-mail address',
                    className: 'form-control'
                },
                {
                    type: 'text',
                    label: 'Phone number',
                    className: 'form-control'
                },
            ]
        }, ]
    };

    return options;
}
